package com.syntax.class01;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing whole number values;
		byte item1=12;
		short item2=123;
		int catalogue1=123123; //popular one
		long catalogue2=123123123123L; //credit cards
		
		//storing and representing decimal values;
		float gram=1.23F;
		double kilogram=123.0;
		
		//storing single characters
		char filler='F';
		char euro= 'â‚¬';
		
		//storing yes or no value
		boolean up=true;
		boolean down=false;
		System.out.println(item1);
		System.out.println(item2); 
		System.out.println(catalogue1);
		System.out.println(catalogue2); 
		System.out.println(gram);
		System.out.println(kilogram);
		System.out.println(filler);
		System.out.println(euro);
		System.out.println(up);
		System.out.println(down);
		
		
		item1=22;
		item2=222;
		catalogue1=22222;
		catalogue2=22222222222L; 
		gram=2.22F;
		kilogram=222.2;
		filler='R';
		euro= 'E';
		up=true;
		down=false;
		System.out.println(item1);
		System.out.println(item2); 
		System.out.println(catalogue1);
		System.out.println(catalogue2); 
		System.out.println(gram);
		System.out.println(kilogram);
		System.out.println(filler);
		System.out.println(euro);
		System.out.println(up);
		System.out.println(down);

	}

}
